# dyanamic tip calculatorprint("welcome to tip calculator")total_bill = float(input("what was the total bill? $"))tip_percentage = float(input("what percentage of tip would you like to give ?"))total_persons = int(input("how many people to split the bill ?"))total_amount = float(total_bill*(1+(tip_percentage/100)))individual_amount = "{:.2f}".format(float(total_amount/total_persons))print(f"total amount is {'{:.2f}'.format(total_amount)}, each person should pay {individual_amount}")